import { Link } from 'react-router-dom';
import { useState, useEffect } from 'react';

const userToken = null;

const Navbar = ({buttonValue, linkValue, userToken}) => {

    u
  const [userConnected, setUserConnected] = useState(null);

  const checkUserConnected = () =>
  {
    if (!userToken)
    {
      setUserConnected(false);
      return;
    }
    const myHeaders = new Headers();
    myHeaders.append('Content-Type', 'application/json');
    myHeaders.append('Authorization', userToken);

    fetch("/user/is-connected",
    {
      method: 'GET',
      headers: myHeaders
    }).then(setUserConnected(true)).catch(setUserConnected(false))
  };

  if (userConnected)
  {
    buttonValue = 'Se deconnecter';
    linkValue = '';
  }

  useEffect(checkUserConnected ,[]);

    return (
        <div className="container">
            <nav className="navbar">
                <Link to="/" style={{textDecoration: 'none'}}>
                    <h4 id='logo'>MCS</h4>
                </Link>
                <Link to={`/${linkValue}`}>
                    <button id="connection-btn">{buttonValue}</button>
                </Link>
            </nav>
        </div>
    );
}

export default Navbar;
