import Home from "./Home";
import Loading from "./Loading";
import Navbar from "./Navbar";
import { useState, useEffect } from 'react';

function App({userToken}) {

  console.log(u)
  const [data, setData] = useState(null);
  const [userConnected, setUserConnected] = useState(null);

  const checkUserConnected = () =>
  {
    if (!userToken)
    {
      setUserConnected(false);
      return;
    }
    const myHeaders = new Headers();
    myHeaders.append('Content-Type', 'application/json');
    myHeaders.append('Authorization', userToken);

    fetch("/user/is-connected",
    {
      method: 'GET',
      headers: myHeaders
    }).then(setUserConnected(true)).catch(setUserConnected(false))
  }

  const fetchData = () =>
  {
      fetch("/posts")
      .then((res) => res.json())
      .then((result) => setData(result))
      .catch((err) => console.log(err));
  }

  useEffect(fetchData ,[]);
  useEffect(checkUserConnected ,[]);



  return (
    <>
      {  userConnected === null ? <></> : 
          userConnected  ?
          <Navbar buttonValue={"Se deconnecter"} linkValue={""}/> 
          : <Navbar buttonValue={"Se connecter"} linkValue={"connexion"}/>
      }
      {data === null ? <Loading /> : <Home data={data}/>}
    </>
  );
}

export default App;
