import { Link } from 'react-router-dom';
import { useState, useEffect } from 'react';
import { ForumState } from './ForumContext';

const Navbar = () => {
  const { userToken, setUserToken } = ForumState();
  const [userConnected, setUserConnected] = useState(false);

  const checkUserConnected = () =>
  {
    if (!userToken)
    {
      setUserToken(false);
      return;
    }
    const myHeaders = new Headers();
    myHeaders.append('Content-Type', 'application/json');
    myHeaders.append('Authorization', userToken);

    fetch("/user/is-connected",
    {
      method: 'GET',
      headers: myHeaders
    }).then((res) => {
        if (res.status() == 200)
            setUserConnected(true);
        else
            setUserConnected(false);
    }).catch(setUserConnected(false))
  }

    return (
        <div className="container">
            <nav className="navbar">
                <Link to="/" style={{textDecoration: 'none'}}>
                    <h4 id='logo'>MCS</h4>
                </Link>
                <Link to={`/${!userConnected ? "connexi" : "Se deconnecter"}`}>
                    <button id="connection-btn">{!userConnected ? "Se connecter" : "Se deconnecter"}</button>
                </Link>
            </nav>
        </div>
    );
}

export default Navbar;
