          userConnected  ?
          <Navbar buttonValue={"Se deconnecter"} linkValue={""}/> 
          : <Navbar buttonValue={"Se connecter"} linkValue={"connexion"}/>
      }
      {data === null ? <Loading /> : <Home data={data}/>}
    </>
  );
}

export default App;
