                        return (<Post post={post}
                            current={currentPage}
                            key={post.id}/>);
                        })}

                </div>
                <div className="forum-right">
                    <div className="forum-top-post">
                        <TopPost posts={posts} />
                    </div>

                    <div className="top-post">
                        <p id="user-count">1000</p>
                    </div>
                </div>
            </div>
        </main>
    );
}

export default { Home };
