import { Link } from "react-router-dom";
import { MaxPost } from "./Home"

const TopPost = ({posts}) => {
    let pageIndex = 0;
    return (
        <div className="container-top-post">
            <div className="container-top-title">
                <h3>TOP POSTS</h3>
            </div>
            {
                posts.map((index, post) => 
                {
                    if (index % MaxPost === 0)
                        pageIndex++;

                    const { id, author, title, content, last_update, response_number, category } = post;
                    return (
                        <Link to={`/posts/${id}`} style={{textDecoration: 'none'}}>
                            <div class="container-post" key={id}>
                                <p>LOGO </p>
                                <p>{title}</p>
                            </div>
                        </Link>
                    );
                })
            }



        </div>
    );
}

export default TopPost;
