    console.log(post.id);
    return ( 
        <Link to={`/posts/${id}`} className="post-link" style={{textDecoration: 'none'}}>
           <div className="post-container">
            {/* <img src={URL_LOGO} className="post-logo"></img> */}
            <p className="post-category">LOGO</p>
            <p className="post-title">{title.length < 50 ? title : title.substring(0, 50) + "..."}</p>
            <p className="post-author">{author}</p>
            <p className="post-responce-number">{response_number}</p>
            <p className="post-last-update">{last_update}</p>
           </div>
        </Link>
    );
}

export default Post;
