import { Navigate } from 'react-router-dom';
import { ForumState } from './ForumContext';

const Responce = ({id_}) => {

    const history = useNavigate();
    const { userToken, userName } = ForumState();
    const handleClick = async (content) =>
    {
        if (!userToken)
            return;

        var seen = [];
        const requestContent = {
            method: 'PATCH',
            headers: {
                'Content-Type' : 'application/json',
                'auth-token': userToken
            },
            body: JSON.stringify(
                {
                    name: userName,
                    content: content,
                    id_: id_,
                },
                (key, val) => {
                    if (val != null && typeof val == "object") {
                        if (seen.indexOf(val) >= 0) {
                            return;
                        }
                        seen.push(val);
                    }
                    return val;
                }
            )
        };
        await fetch("/posts/add-comment", requestContent).then((_) => history(0));
    }

    const [ content, setContent ] = useStas
    return (
        <div className="responce-container">
            <h2>Ajouter un commentaire</h2>
            <textarea/>
            <button>Poster</button>
        </div>
    );
}

export default Responce;
