import { Link } from 'react-router-dom'
import Navbar from "./Navbar";
import { useState } from 'react';

const CreatePost = () => {
    const handleClick = (title1, content1, author1, category1) =>
    {
        const requestContent = {
            method: 'POST',
            headers: {'Content-Type' : 'application/json'},
            body: JSON.stringify(
                {
                    author: author,
                    title: title,
                    content: content,
                    category: category,
                    responces: []
                }
            )
        };
        fetch("/posts/create-new-post", requestContent).then(val => console.log(val));
    }
    const [title, setTitle] = useState("");
    const [content, setContent] = useState("");
    return (
        <>
            <Navbar buttonValue={"Se connecter"} linkValue={"connexion"} />
            <div className="create-post-container">
                <h2>Creation du post</h2>
                <p>Titre</p>
                <input value={title} onChange={(event) => setTitle(event.target.value)} type="text" placeholder="Bons plans d'Italie" />

                <p>Contenue</p>
                <textarea value={content} onChange={(event) => setContent(event.target.value)}/>
                <Link to='/'>
                    <button id='create-post-btn' onClick={(title, content) => handleClick(title, content, "nothing", "Voyage")}>Creer</button>
                </Link>
            </div>
        </>

    );
}

export default CreatePost;
