import Home from "./Home";
import Loading from "./Loading";
import Navbar from "./Navbar";
import { useState, useEffect } from 'react';
import { ForumState } from './ForumContext';

function App() {

  const [data, setData] = useState(null);
  const [userConnected, setUserConnected] = useState(null);
  const { userToken, setUserToken } = ForumState();
  console.log(user)

  const checkUserConnected = () =>
  {
    if (!userToken)
    {
      setUserToken(false);
      return;
    }
    const myHeaders = new Headers();
    myHeaders.append('Content-Type', 'application/json');
    myHeaders.append('Authorization', userToken);

    fetch("/user/is-connected",
    {
      method: 'GET',
      headers: myHeaders
    }).then(setUserConnected(true)).catch(setUserConnected(false))
  }

  const fetchData = () =>
  {
      fetch("/posts")
      .then((res) => res.json())
      .then((result) => setData(result))
      .catch((err) => console.log(err));
  }

  useEffect(fetchData ,[]);
  useEffect(checkUserConnected ,[]);


  console.log()
  return (
    <>
      <Navbar buttonValue={"Se connecter"} linkValue={"connexion"}/>
      {data === null ? <Loading /> : <Home data={data}/>}
    </>
  );
}

export default App;
